import React, { useState } from "react";
import { jsPDF } from "jspdf";
import autoTable from "jspdf-autotable";
import "jspdf-autotable";
import '../components/InvoiceGenerator.css';

const InvoiceGenerator = () => {
  const [invoiceNo, setInvoiceNo] = useState(1);
  const [invoiceDate, setInvoiceDate] = useState("");
  const [clientName, setClientName] = useState("");
  const [clientGST, setClientGST] = useState("");
  const [clientAddress, setClientAddress] = useState("");
  const [items, setItems] = useState([
    { id: 1, particulars: "", qty: 0, rate: 0, amount: 0 }
  ]);
  const [previousBills, setPreviousBills] = useState([]);

  const addItem = () => {
    setItems([...items, { id: items.length + 1, particulars: "", qty: 0, rate: 0, amount: 0 }]);
  };

  const deleteItem = (id) => {
    setItems(items.filter(item => item.id !== id));
  };

  const handleItemChange = (index, field, value) => {
    const newItems = [...items];
    newItems[index][field] = value;
    newItems[index].amount = newItems[index].qty * newItems[index].rate;
    setItems(newItems);
  };

  const calculateTotals = () => {
    const subtotal = items.reduce((sum, item) => sum + item.amount, 0);
    const cgst = subtotal * 0.025;
    const sgst = subtotal * 0.025;
    const grandTotal = subtotal + cgst + sgst;
    return { subtotal, cgst, sgst, grandTotal };
  };

  const saveBill = () => {
    const newBill = {
      invoiceNo,
      invoiceDate,
      clientName,
      clientGST,
      clientAddress,
      items,
      totals: calculateTotals()
    };
    const storedBills = JSON.parse(localStorage.getItem("bills")) || [];
    storedBills.push(newBill);
    localStorage.setItem("bills", JSON.stringify(storedBills));
    alert("Bill saved successfully!");
  };

  const viewPreviousBills = () => {
    const storedBills = JSON.parse(localStorage.getItem("bills")) || [];
    setPreviousBills(storedBills);
  };

  const downloadPDF = () => {
    const doc = new jsPDF();
    doc.text("Sadguru Cloth Center", 10, 10);
    autoTable(doc, {
      head: [["Sr. No", "Particulars", "Qty", "Rate", "Amount"]],
      body: items.map(item => [item.id, item.particulars, item.qty, item.rate, item.amount])
    });
    doc.save("invoice.pdf");
  };

  const { subtotal, cgst, sgst, grandTotal } = calculateTotals();

  return (
    <div className="invoice-container">
      <h2>Sadguru Cloth Center</h2>
      <h3>Tax Invoice</h3>
      <div className="invoice-details">
        <label>To: <input type="text" value={clientName} onChange={(e) => setClientName(e.target.value)} /></label>
        <label>GST No: <input type="text" value={clientGST} onChange={(e) => setClientGST(e.target.value)} /></label>
        <label className="full-width">Address: <textarea value={clientAddress} onChange={(e) => setClientAddress(e.target.value)} /></label>
        <label>Invoice No: <input type="number" value={invoiceNo} onChange={(e) => setInvoiceNo(e.target.value)} /></label>
        <label>Date: <input type="date" value={invoiceDate} onChange={(e) => setInvoiceDate(e.target.value)} /></label>
      </div>
      
      <table>
        <thead>
          <tr>
            <th>Sr. No</th>
            <th>Particulars</th>
            <th>Qty</th>
            <th>Rate</th>
            <th>Amount</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td><input type="text" value={item.particulars} onChange={(e) => handleItemChange(index, "particulars", e.target.value)} /></td>
              <td><input type="number" value={item.qty} onChange={(e) => handleItemChange(index, "qty", parseFloat(e.target.value))} /></td>
              <td><input type="number" value={item.rate} onChange={(e) => handleItemChange(index, "rate", parseFloat(e.target.value))} /></td>
              <td>{item.amount}</td>
              <td><button onClick={() => deleteItem(item.id)}>Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
      
      <button onClick={addItem}>Add Item</button>
      <button onClick={saveBill}>Save Bill</button>
      <button onClick={viewPreviousBills}>Previous Bills</button>
      
      <div className="total-section">
        <p>Subtotal: {subtotal.toFixed(2)}</p>
        <p>CGST @ 2.5%: {cgst.toFixed(2)}</p>
        <p>SGST @ 2.5%: {sgst.toFixed(2)}</p>
        <p><b>Grand Total: {grandTotal.toFixed(2)}</b></p>
      </div>
      
      <button onClick={() => window.print()}>Print</button>
      <button onClick={downloadPDF}>Download PDF</button>
      
      {previousBills.length > 0 && (
        <div className="previous-bills">
          <h3>Previous Bills</h3>
          <ul>
            {previousBills.map((bill, index) => (
              <li key={index}>Invoice {bill.invoiceNo} - {bill.clientName} - â‚¹{bill.totals.grandTotal.toFixed(2)}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default InvoiceGenerator;
