import React, { useState } from "react";
import { jsPDF } from "jspdf";
import "jspdf-autotable";
import "./InvoiceGenerator.css"; // Create a CSS file for styling

const InvoiceGenerator = () => {
  const [invoiceNo, setInvoiceNo] = useState(1);
  const [clientName, setClientName] = useState("");
  const [gstNum, setGstNum] = useState("");
  const [clientAdd, setClientAdd] = useState("");
  const [invoiceDate, setInvoiceDate] = useState("");
  const [items, setItems] = useState([
    { id: 1, particulars: "", qty: 0, rate: 0, amount: 0 },
  ]);

  // Function to handle item changes
  const handleItemChange = (index, field, value) => {
    const updatedItems = [...items];
    updatedItems[index][field] = value;
    updatedItems[index].amount = updatedItems[index].qty * updatedItems[index].rate;
    setItems(updatedItems);
  };

  // Function to add a new row
  const addItem = () => {
    setItems([
      ...items,
      { id: items.length + 1, particulars: "", qty: 0, rate: 0, amount: 0 },
    ]);
  };

  // Function to remove an item
  const removeItem = (index) => {
    const updatedItems = items.filter((_, i) => i !== index);
    setItems(updatedItems);
  };

  // Function to calculate totals
  const calculateTotals = () => {
    const subtotal = items.reduce((sum, item) => sum + item.amount, 0);
    const cgst = subtotal * 0.025;
    const sgst = subtotal * 0.025;
    const grandTotal = subtotal + cgst + sgst;
    return { subtotal, cgst, sgst, grandTotal };
  };

  // Function to generate PDF
  const downloadPDF = () => {
    const doc = new jsPDF();
    doc.text("Sadguru Cloth Center", 20, 10);
    doc.text(`Invoice No: ${invoiceNo}`, 20, 20);
    doc.text(`Date: ${invoiceDate}`, 150, 20);
    doc.text(`Client: ${clientName}`, 20, 30);
    doc.text(`GST No: ${gstNum}`, 20, 40);
    doc.text(`Address: ${clientAdd}`, 20, 50);

    doc.autoTable({
      startY: 60,
      head: [["Sr. No", "Particulars", "Qty", "Rate", "Amount"]],
      body: items.map((item, index) => [
        index + 1,
        item.particulars,
        item.qty,
        item.rate,
        item.amount,
      ]),
    });

    const { subtotal, cgst, sgst, grandTotal } = calculateTotals();
    doc.text(`Subtotal: ₹${subtotal.toFixed(2)}`, 20, doc.lastAutoTable.finalY + 10);
    doc.text(`CGST (2.5%): ₹${cgst.toFixed(2)}`, 20, doc.lastAutoTable.finalY + 20);
    doc.text(`SGST (2.5%): ₹${sgst.toFixed(2)}`, 20, doc.lastAutoTable.finalY + 30);
    doc.text(`Grand Total: ₹${grandTotal.toFixed(2)}`, 20, doc.lastAutoTable.finalY + 40);

    doc.save(`Invoice_${invoiceNo}.pdf`);
  };

  const { subtotal, cgst, sgst, grandTotal } = calculateTotals();

  return (
    <div className="invoice-container">
      <h2>Sadguru Cloth Center</h2>
      <p><strong>Tax Invoice</strong></p>
      
      <div className="invoice-details">
        <label>
          To: <input type="text" value={clientName} onChange={(e) => setClientName(e.target.value)} />
        </label>
        <label>
          GST No: <input type="text" value={gstNum} onChange={(e) => setGstNum(e.target.value)} />
        </label>
        <label className="full-width">
          Address: <textarea value={clientAdd} onChange={(e) => setClientAdd(e.target.value)} />
        </label>
        <label>
          Invoice No: <input type="number" value={invoiceNo} readOnly />
        </label>
        <label>
          Date: <input type="date" value={invoiceDate} onChange={(e) => setInvoiceDate(e.target.value)} />
        </label>
      </div>

      <table className="invoice-table">
        <thead>
          <tr>
            <th>Sr. No</th>
            <th>Particulars</th>
            <th>Qty</th>
            <th>Rate</th>
            <th>Amount</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={item.id}>
              <td>{index + 1}</td>
              <td>
                <input
                  type="text"
                  value={item.particulars}
                  onChange={(e) => handleItemChange(index, "particulars", e.target.value)}
                />
              </td>
              <td>
                <input
                  type="number"
                  value={item.qty}
                  onChange={(e) => handleItemChange(index, "qty", parseInt(e.target.value) || 0)}
                />
              </td>
              <td>
                <input
                  type="number"
                  value={item.rate}
                  onChange={(e) => handleItemChange(index, "rate", parseFloat(e.target.value) || 0)}
                />
              </td>
              <td>{item.amount.toFixed(2)}</td>
              <td>
                <button onClick={() => removeItem(index)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={addItem}>Add Item</button>

      <div className="totals">
        <p>Subtotal: ₹{subtotal.toFixed(2)}</p>
        <p>CGST (2.5%): ₹{cgst.toFixed(2)}</p>
        <p>SGST (2.5%): ₹{sgst.toFixed(2)}</p>
        <p><b>Grand Total: ₹{grandTotal.toFixed(2)}</b></p>
      </div>

      <button onClick={downloadPDF}>Download PDF</button>
    </div>
  );
};

export default InvoiceGenerator;
