import React, { useState } from "react";

const Invoice = () => {
  const [items, setItems] = useState([{ particulars: "", hsn: "", qty: 0, rate: 0 }]);
  const [invoiceNo, setInvoiceNo] = useState(1);
  const [to, setTo] = useState("");
  const [gstNo, setGstNo] = useState("");
  const [date, setDate] = useState(new Date().toISOString().substr(0, 10));

  // ✅ Correct number to words in Indian System
  const numberToWords = (num) => {
    if (num === 0) return "Zero Rupees Only";

    const ones = ["", "One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine",
      "Ten", "Eleven", "Twelve", "Thirteen", "Fourteen", "Fifteen", "Sixteen",
      "Seventeen", "Eighteen", "Nineteen"];
    const tens = ["", "", "Twenty", "Thirty", "Forty", "Fifty", "Sixty", "Seventy", "Eighty", "Ninety"];

    const convertBelowHundred = (n) => {
      if (n < 20) return ones[n];
      return tens[Math.floor(n / 10)] + (n % 10 ? " " + ones[n % 10] : "");
    };

    const convertBelowThousand = (n) => {
      let str = "";
      if (Math.floor(n / 100) > 0) {
        str += ones[Math.floor(n / 100)] + " Hundred ";
        n %= 100;
      }
      if (n > 0) str += convertBelowHundred(n);
      return str.trim();
    };

    let result = "";
    let integerPart = Math.floor(num);
    let decimalPart = Math.round((num - integerPart) * 100);

    const crore = Math.floor(integerPart / 10000000);
    integerPart %= 10000000;
    const lakh = Math.floor(integerPart / 100000);
    integerPart %= 100000;
    const thousand = Math.floor(integerPart / 1000);
    integerPart %= 1000;
    const hundred = integerPart;

    if (crore > 0) result += convertBelowThousand(crore) + " Crore ";
    if (lakh > 0) result += convertBelowThousand(lakh) + " Lakh ";
    if (thousand > 0) result += convertBelowThousand(thousand) + " Thousand ";
    if (hundred > 0) result += convertBelowThousand(hundred);

    result = result.trim() + " Rupees";
    if (decimalPart > 0) {
      result += " and " + convertBelowHundred(decimalPart) + " Paise";
    }
    return result + " Only";
  };

  const handleItemChange = (index, field, value) => {
    const updatedItems = [...items];
    updatedItems[index][field] = field === "qty" || field === "rate" ? Number(value) : value;
    setItems(updatedItems);
  };

  const addItem = () => {
    setItems([...items, { particulars: "", hsn: "", qty: 0, rate: 0 }]);
  };

  const subtotal = items.reduce((acc, item) => acc + item.qty * item.rate, 0);
  const cgst = subtotal * 0.09;
  const sgst = subtotal * 0.09;
  const grandTotal = subtotal + cgst + sgst;

  return (
    <div style={{ padding: "20px", maxWidth: "900px", margin: "auto", fontFamily: "Arial" }}>
      <div style={{ border: "1px solid #000", padding: "20px" }}>
        {/* Header */}
        <div style={{ textAlign: "center", marginBottom: "20px" }}>
          <img src="/logo.png" alt="Logo" style={{ height: "80px" }} />
          <h2 style={{ margin: "5px 0", color: "red" }}>SHRI G ENTERPRISES</h2>
          <h4 style={{ margin: "5px 0", color: "blue" }}>Welding & Safety Equipments</h4>
          <p>
            Branch: S.No.371, Flat No.20, Unity Park, Somwar Peth, Narpatgiri Chowk, Above HDFC Bank, Pune 411011.
          </p>
          <p>Email: shrigenterprises25@gmail.com | Mob: 9850111166</p>
          <p>GSTIN: 27AJIPG2516N1Z2</p>
        </div>

        {/* Invoice Info */}
        <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "20px" }}>
          <div>
            <p><strong>Invoice No:</strong> {invoiceNo}</p>
            <p><strong>To:</strong> <input value={to} onChange={(e) => setTo(e.target.value)} /></p>
            <p><strong>GST No:</strong> <input value={gstNo} onChange={(e) => setGstNo(e.target.value)} /></p>
          </div>
          <div>
            <p><strong>Date:</strong> <input type="date" value={date} onChange={(e) => setDate(e.target.value)} /></p>
          </div>
        </div>

        {/* Items Table */}
        <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: "20px" }}>
          <thead>
            <tr style={{ background: "#007bff", color: "#fff" }}>
              <th>SR. NO</th>
              <th>PARTICULARS</th>
              <th>HSN / SAC</th>
              <th>QTY</th>
              <th>RATE</th>
              <th>AMOUNT</th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, index) => (
              <tr key={index} style={{ borderBottom: "1px solid #ddd" }}>
                <td>{index + 1}</td>
                <td>
                  <input
                    value={item.particulars}
                    onChange={(e) => handleItemChange(index, "particulars", e.target.value)}
                  />
                </td>
                <td>
                  <input
                    value={item.hsn}
                    onChange={(e) => handleItemChange(index, "hsn", e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={item.qty}
                    onChange={(e) => handleItemChange(index, "qty", e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={item.rate}
                    onChange={(e) => handleItemChange(index, "rate", e.target.value)}
                  />
                </td>
                <td>{(item.qty * item.rate).toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <button onClick={addItem}>+ Add Item</button>

        {/* Totals */}
        <div style={{ marginTop: "20px" }}>
          <p><strong>Subtotal:</strong> {subtotal.toFixed(2)}</p>
          <p><strong>CGST @ 9%:</strong> {cgst.toFixed(2)}</p>
          <p><strong>SGST @ 9%:</strong> {sgst.toFixed(2)}</p>
