import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";

const PreviousBills = () => {
  const [previousBills, setPreviousBills] = useState([]);
  const history = useHistory();

  useEffect(() => {
    const savedBills = JSON.parse(localStorage.getItem("bills")) || [];
    setPreviousBills(savedBills);
  }, []);

  // Navigate to the full bill details page
  const viewBill = (bill) => {
    history.push({
      pathname: `/view-bill/${bill.invoiceNo}`,
      state: { bill }
    });
  };

  return (
    <div className="previous-bills">
      <h2>Previous Bills</h2>
      {previousBills.length === 0 ? (
        <p>No previous bills available.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Invoice No</th>
              <th>Client</th>
              <th>Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {previousBills.map((bill) => (
              <tr key={bill.invoiceNo}>
                <td>{bill.invoiceNo}</td>
                <td>{bill.clientName}</td>
                <td>{bill.invoiceDate}</td>
                <td>
                  <button onClick={() => viewBill(bill)}>View Bill</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PreviousBills;
