import React, { useState } from "react";
import { TextField, Button, Box, Typography } from "@mui/material";
import { createInvoice } from "../api/invoiceApi";

const InvoiceForm = ({ onInvoiceCreated }) => {
  const [formData, setFormData] = useState({
    customerName: "",
    gstNumber: "",
    address: "",
    items: [{ description: "", quantity: 1, rate: 0, amount: 0 }],
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleItemChange = (index, field, value) => {
    const updatedItems = [...formData.items];
    updatedItems[index][field] = value;
    updatedItems[index].amount = updatedItems[index].quantity * updatedItems[index].rate;
    setFormData({ ...formData, items: updatedItems });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const subtotal = formData.items.reduce((acc, item) => acc + item.amount, 0);
    const cgst = subtotal * 0.09;
    const sgst = subtotal * 0.09;
    const grandTotal = subtotal + cgst + sgst;

    const newInvoice = {
      ...formData,
      subtotal,
      cgst,
      sgst,
      grandTotal,
    };

    const createdInvoice = await createInvoice(newInvoice);
    onInvoiceCreated(createdInvoice);
  };

  return (
    <Box sx={{ maxWidth: 500, margin: "auto", padding: 3, border: "1px solid #ddd", borderRadius: 2 }}>
      <Typography variant="h6">Create Invoice</Typography>
      <TextField fullWidth margin="normal" label="Customer Name" name="customerName" onChange={handleChange} />
      <TextField fullWidth margin="normal" label="GST Number" name="gstNumber" onChange={handleChange} />
      <TextField fullWidth margin="normal" label="Address" name="address" onChange={handleChange} />

      {formData.items.map((item, index) => (
        <Box key={index} sx={{ display: "flex", gap: 2, marginBottom: 2 }}>
          <TextField label="Description" onChange={(e) => handleItemChange(index, "description", e.target.value)} />
          <TextField label="Quantity" type="number" onChange={(e) => handleItemChange(index, "quantity", Number(e.target.value))} />
          <TextField label="Rate" type="number" onChange={(e) => handleItemChange(index, "rate", Number(e.target.value))} />
        </Box>
      ))}

      <Button variant="contained" color="primary" onClick={handleSubmit}>
        Submit
      </Button>
    </Box>
  );
};

export default InvoiceForm;
