import React, { useState } from "react";
import { Table, Input, Button, DatePicker, Typography } from "antd";
import jsPDF from "jspdf";
import "jspdf-autotable";
import "./Invoice.css";

const { Title, Text } = Typography;

const InvoiceGenerator = () => {
  const [invoiceNo, setInvoiceNo] = useState(1);
  const [invoiceDate, setInvoiceDate] = useState(null);
  const [clientName, setClientName] = useState("");
  const [gstNum, setGstNum] = useState("");
  const [clientAddress, setClientAddress] = useState("");
  const [items, setItems] = useState([{ key: 1, particulars: "", qty: 1, rate: 0, amount: 0 }]);

  const handleItemChange = (key, field, value) => {
    const updatedItems = items.map((item) =>
      item.key === key ? { ...item, [field]: value, amount: item.qty * item.rate } : item
    );
    setItems(updatedItems);
  };

  const addItem = () => {
    setItems([...items, { key: items.length + 1, particulars: "", qty: 1, rate: 0, amount: 0 }]);
  };

  const deleteItem = (key) => {
    setItems(items.filter((item) => item.key !== key));
  };

  const calculateTotals = () => {
    const subtotal = items.reduce((sum, item) => sum + item.amount, 0);
    const cgst = subtotal * 0.025;
    const sgst = subtotal * 0.025;
    const grandTotal = subtotal + cgst + sgst;
    return { subtotal, cgst, sgst, grandTotal };
  };

  const downloadPDF = () => {
    const doc = new jsPDF();
    doc.text("Invoice", 90, 10);
    doc.autoTable({
      startY: 20,
      head: [["Particulars", "Qty", "Rate", "Amount"]],
      body: items.map(({ particulars, qty, rate, amount }) => [particulars, qty, rate, amount]),
    });
    doc.save(`Invoice_${invoiceNo}.pdf`);
  };

  const { subtotal, cgst, sgst, grandTotal } = calculateTotals();

  return (
    <div className="invoice-container">
      <Title level={2}>Sadguru Cloth Center</Title>
      <Text strong>Mfg. of Hospital Garments, Plastic Aprons & Rexine</Text>
      <br />
      <Text strong>Mob:</Text> 9881454802 | <Text strong>Office:</Text> 26351192
      <br />
      <Text strong>Branch:</Text> 264, Nana Peth, Pune-411002
      <br />
      <Title level={3}>Tax Invoice</Title>
      <div className="invoice-details">
        <Input placeholder="Client Name" value={clientName} onChange={(e) => setClientName(e.target.value)} />
        <Input placeholder="GST No" value={gstNum} onChange={(e) => setGstNum(e.target.value)} />
        <Input placeholder="Address" value={clientAddress} onChange={(e) => setClientAddress(e.target.value)} />
        <Input type="number" value={invoiceNo} onChange={(e) => setInvoiceNo(e.target.value)} />
        <DatePicker onChange={(date) => setInvoiceDate(date)} />
      </div>
      <Table dataSource={items} pagination={false} rowKey="key">
        <Table.Column title="Particulars" dataIndex="particulars" render={(text, record) => (
          <Input value={text} onChange={(e) => handleItemChange(record.key, "particulars", e.target.value)} />
        )} />
        <Table.Column title="Qty" dataIndex="qty" render={(text, record) => (
          <Input type="number" value={text} onChange={(e) => handleItemChange(record.key, "qty", Number(e.target.value))} />
        )} />
        <Table.Column title="Rate" dataIndex="rate" render={(text, record) => (
          <Input type="number" value={text} onChange={(e) => handleItemChange(record.key, "rate", Number(e.target.value))} />
        )} />
        <Table.Column title="Amount" dataIndex="amount" render={(text) => <Text>{text.toFixed(2)}</Text>} />
        <Table.Column title="Action" render={(text, record) => (
          <Button danger onClick={() => deleteItem(record.key)}>Delete</Button>
        )} />
      </Table>
      <Button onClick={addItem} style={{ marginTop: 10 }}>Add Item</Button>
      <div className="total-section">
        <Text>Subtotal: ₹{subtotal.toFixed(2)}</Text>
        <br />
        <Text>CGST @ 2.5%: ₹{cgst.toFixed(2)}</Text>
        <br />
        <Text>SGST @ 2.5%: ₹{sgst.toFixed(2)}</Text>
        <br />
        <Title level={4}>Grand Total: ₹{grandTotal.toFixed(2)}</Title>
      </div>
      <Button onClick={downloadPDF} type="primary" style={{ marginTop: 10 }}>Download PDF</Button>
    </div>
  );
};

export default InvoiceGenerator;
