import axios from "axios";

const API_URL = "http://localhost:8080/api/invoices"; // Your Backend URL

// Fetch all invoices
export const fetchInvoices = async () => {
  const response = await axios.get(API_URL);
  return response.data;
};

// Create a new invoice
export const createInvoice = async (invoiceData) => {
  const response = await axios.post(API_URL, invoiceData);
  return response.data;
};

// Search invoice by customer name
export const searchInvoices = async (name) => {
  const response = await axios.get(`${API_URL}/search/${name}`);
  return response.data;
};

// Download invoice as PDF (Placeholder)
export const downloadInvoice = async (id) => {
  const response = await axios.get(`${API_URL}/download/${id}`);
  return response.data;
};
