import React, { useState } from "react";
import { jsPDF } from "jspdf";
import autoTable from "jspdf-autotable";
import "./InvoiceGenerator.css";

const InvoiceGenerator = () => {
  const [invoiceData, setInvoiceData] = useState({
    customerName: "",
    date: "",
    items: [{ description: "", quantity: 1, price: 0 }],
  });

  const handleInputChange = (index, event) => {
    const values = [...invoiceData.items];
    values[index][event.target.name] = event.target.value;
    setInvoiceData({ ...invoiceData, items: values });
  };

  const addItem = () => {
    setInvoiceData({
      ...invoiceData,
      items: [...invoiceData.items, { description: "", quantity: 1, price: 0 }],
    });
  };

  const calculateTotal = () => {
    return invoiceData.items.reduce((total, item) => total + item.quantity * item.price, 0);
  };

  const downloadPDF = () => {
    const doc = new jsPDF();
    doc.text("Invoice", 14, 15);
    doc.text(`Customer: ${invoiceData.customerName}`, 14, 25);
    doc.text(`Date: ${invoiceData.date}`, 14, 35);

    autoTable(doc, {
      startY: 45,
      head: [["Description", "Quantity", "Price", "Total"]],
      body: invoiceData.items.map((item) => [
        item.description,
        item.quantity,
        item.price,
        item.quantity * item.price,
      ]),
    });

    doc.text(`Total: ₹${calculateTotal()}`, 14, doc.autoTable.previous.finalY + 10);
    doc.save("invoice.pdf");
  };

  return (
    <div className="invoice-container">
      <h2>Invoice Generator</h2>
      <label>Customer Name:</label>
      <input
        type="text"
        value={invoiceData.customerName}
        onChange={(e) => setInvoiceData({ ...invoiceData, customerName: e.target.value })}
      />
      <label>Date:</label>
      <input
        type="date"
        value={invoiceData.date}
        onChange={(e) => setInvoiceData({ ...invoiceData, date: e.target.value })}
      />
      <h3>Items</h3>
      {invoiceData.items.map((item, index) => (
        <div key={index} className="item-row">
          <input
            type="text"
            name="description"
            placeholder="Item Description"
            value={item.description}
            onChange={(e) => handleInputChange(index, e)}
          />
          <input
            type="number"
            name="quantity"
            placeholder="Quantity"
            value={item.quantity}
            onChange={(e) => handleInputChange(index, e)}
          />
          <input
            type="number"
            name="price"
            placeholder="Price"
            value={item.price}
            onChange={(e) => handleInputChange(index, e)}
          />
        </div>
      ))}
      <button onClick={addItem}>Add Item</button>
      <h3>Total: ₹{calculateTotal()}</h3>
      <button onClick={downloadPDF}>Download Invoice</button>
    </div>
  );
};

export default InvoiceGenerator;
