import React, { useState, useEffect } from "react";
import axios from "axios";
import { Button, Table, Input, Select, Card } from "antd";
import "./Invoice.css";

const { Option } = Select;

const InvoiceGenerator = () => {
  const [customers, setCustomers] = useState([]);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [invoice, setInvoice] = useState({
    customerName: "",
    gstNumber: "",
    address: "",
    items: [],
    subtotal: 0,
    cgst: 0,
    sgst: 0,
    grandTotal: 0,
  });

  useEffect(() => {
    axios.get("http://localhost:8080/api/customers").then((res) => {
      setCustomers(res.data);
    });
  }, []);

  const handleCustomerSelect = (value) => {
    const customer = customers.find((c) => c._id === value);
    setInvoice({
      ...invoice,
      customerName: customer.name,
      gstNumber: customer.gstNumber,
      address: customer.address,
    });
  };

  const addItem = () => {
    setInvoice({
      ...invoice,
      items: [...invoice.items, { description: "", quantity: 1, rate: 0, amount: 0 }],
    });
  };

  const handleItemChange = (index, field, value) => {
    const updatedItems = [...invoice.items];
    updatedItems[index][field] = value;
    updatedItems[index].amount = updatedItems[index].quantity * updatedItems[index].rate;

    const subtotal = updatedItems.reduce((acc, item) => acc + item.amount, 0);
    const cgst = subtotal * 0.025;
    const sgst = subtotal * 0.025;
    const grandTotal = subtotal + cgst + sgst;

    setInvoice({ ...invoice, items: updatedItems, subtotal, cgst, sgst, grandTotal });
  };

  const saveInvoice = () => {
    axios.post("http://localhost:8080/api/invoices", invoice).then(() => {
      alert("Invoice saved successfully!");
    });
  };

  return (
    <div className="invoice-container">
      <Card className="invoice-card">
        <h2>Invoice Generator</h2>
        <Select
          placeholder="Select Customer"
          onChange={handleCustomerSelect}
          className="customer-select"
        >
          {customers.map((customer) => (
            <Option key={customer._id} value={customer._id}>
              {customer.name}
            </Option>
          ))}
        </Select>
        <Input placeholder="Customer Name" value={invoice.customerName} disabled />
        <Input placeholder="GST Number" value={invoice.gstNumber} disabled />
        <Input placeholder="Address" value={invoice.address} disabled />

        <Table
          dataSource={invoice.items}
          columns={[
            { title: "Description", dataIndex: "description", render: (_, record, index) => (
                <Input
                  value={record.description}
                  onChange={(e) => handleItemChange(index, "description", e.target.value)}
                />
              ),
            },
            { title: "Qty", dataIndex: "quantity", render: (_, record, index) => (
                <Input
                  type="number"
                  value={record.quantity}
                  onChange={(e) => handleItemChange(index, "quantity", Number(e.target.value))}
                />
              ),
            },
            { title: "Rate", dataIndex: "rate", render: (_, record, index) => (
                <Input
                  type="number"
                  value={record.rate}
                  onChange={(e) => handleItemChange(index, "rate", Number(e.target.value))}
                />
              ),
            },
            { title: "Amount", dataIndex: "amount" },
          ]}
        />

        <Button onClick={addItem} className="add-item-btn">Add Item</Button>
        <div className="totals">
          <p>Subtotal: ₹{invoice.subtotal.toFixed(2)}</p>
          <p>CGST (2.5%): ₹{invoice.cgst.toFixed(2)}</p>
          <p>SGST (2.5%): ₹{invoice.sgst.toFixed(2)}</p>
          <h3>Grand Total: ₹{invoice.grandTotal.toFixed(2)}</h3>
        </div>
        <Button onClick={saveInvoice} type="primary" className="save-btn">Save Invoice</Button>
      </Card>
    </div>
  );
};

export default InvoiceGenerator;
