import React, { useState, useEffect } from "react";
import { jsPDF } from "jspdf";
import autoTable from "jspdf-autotable";
import { useNavigate } from 'react-router-dom';
import "../components/InvoiceGenerator.css";
import logo from "../assets/shrig.jpeg";

const InvoiceGenerator = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const savedBills = JSON.parse(localStorage.getItem("bills")) || [];
    if (savedBills.length === 0) {
      localStorage.setItem("invoiceNo", "1");
    }
  }, []);

  const [invoiceNo, setInvoiceNo] = useState(() => {
    const savedInvoiceNo = localStorage.getItem("invoiceNo");
    return savedInvoiceNo ? parseInt(savedInvoiceNo, 10) : 1;
  });

  const [invoiceDate, setInvoiceDate] = useState("");
  const [clientName, setClientName] = useState("");
  const [clientGST, setClientGST] = useState("");
  const [clientAddress, setClientAddress] = useState("");
  const [poNumber, setPoNumber] = useState("");  // ✅ Added PO Number

  const [items, setItems] = useState([
    { id: 1, particulars: "", hsn: "", qty: "", rate: "", amount: 0 }
  ]);

  const handleItemChange = (index, field, value) => {
    const newItems = [...items];
    newItems[index][field] =
      field === "qty" || field === "rate" ? parseFloat(value) || 0 : value;
    newItems[index].amount = newItems[index].qty * newItems[index].rate;
    setItems(newItems);
  };

  const addItem = () =>
    setItems([
      ...items,
      { id: items.length + 1, particulars: "", hsn: "", qty: 0, rate: 0, amount: 0 }
    ]);

  const deleteItem = (id) => setItems(items.filter(item => item.id !== id));

  const calculateTotals = () => {
    const subtotal = items.reduce((sum, item) => sum + item.amount, 0);
    const cgst = subtotal * 0.09;
    const sgst = subtotal * 0.09;
    return { subtotal, cgst, sgst, grandTotal: subtotal + cgst + sgst };
  };

  const saveBill = () => {
    const billData = { 
      invoiceNo, 
      invoiceDate, 
      clientName, 
      clientGST, 
      clientAddress, 
      poNumber,   // ✅ Save PO Number
      items, 
      totals: calculateTotals() 
    };
    const savedBills = JSON.parse(localStorage.getItem("bills")) || [];
    savedBills.push(billData);
    localStorage.setItem("bills", JSON.stringify(savedBills));
    localStorage.setItem("invoiceNo", (invoiceNo + 1).toString());
    setInvoiceNo(invoiceNo + 1);
    setItems([{ id: 1, particulars: "", hsn: "", qty: 0, rate: 0, amount: 0 }]);
    setClientName("");
    setClientGST("");
    setClientAddress("");
    setPoNumber("");  // ✅ Reset PO No
    setInvoiceDate("");
    alert("Bill saved successfully!");
  };

  // numberToWords function remains unchanged...

  return (
    <div className="invoice-container">
      <div className="header">
        <div className="business-info">
          <div>
            <img
              src={logo}
              alt="Business Logo"
              style={{ height: "200px", width:"800px", display: "block", margin: "0 auto" }}
            />
          </div>
          <h3>Tax Invoice</h3>
          <p>Branch: S.No.371, Flat No.20, Unity Park, Somwar Peth, Narpatgiri Chowk, Above HDFC Bank, Pune 411011.</p>
          <p>E-mail Id:- shrigenterprises25@gmail.com</p>
          <p>Mob: 9850111166</p>
          <p>GSTIN: 27AJIPG2516N1Z2</p>
        </div>
      </div>
      
      <div className="invoice-details">
        <label>Invoice No: {invoiceNo}</label>
        <label>To: <input type="text" value={clientName} onChange={(e) => setClientName(e.target.value)} /></label>
        <label>GST No: 
          <input 
            type="text" 
            value={clientGST} 
            onChange={(e) => setClientGST(e.target.value.toUpperCase())} 
            maxLength="15"
            required
          />
        </label>
        <label>
          Address: 
          <textarea 
            value={clientAddress} 
            onChange={(e) => setClientAddress(e.target.value)} 
            className="address-input"
          />
        </label>
        {/* ✅ PO Number field */}
        <label>
          PO No:
          <input 
            type="text" 
            value={poNumber} 
            onChange={(e) => setPoNumber(e.target.value)} 
          />
        </label>
        <label>Date: 
          <input 
            type="date" 
            value={invoiceDate} 
            onChange={(e) => setInvoiceDate(e.target.value)} 
            min="2025-04-01" 
            max="2026-03-31" 
            required 
          />
        </label>
      </div>

      {/* Rest of your code stays same */}
    </div>
  );
};

export default InvoiceGenerator;
